con:config.script:
import com.eviware.soapui.support.GroovyUtils

//defining variables to store value from properties file
def curGBP = context.expand('${Properties#currencyGBP}')
def curIND = context.expand('${Properties#currencyIND}')
def yesterdayDate = context.expand('${Properties#YDate}')
def todayDate = context.expand('${Properties#TDate}')
def amount= context.expand('${Properties#Amount}')

//Storing request xml for Conversion step
def reqXML = context.expand('${Conversion#Request}')

//Modifing the request xml with values from properties 
def gutil = new GroovyUtils(context)
def xmlNode = gutil.getXmlHolder(reqXML)
xmlNode.namespaces['ns1']="http://tempuri.org/"
xmlNode.setNodeValue("//ns1:CurrencyFrom",curGBP)
xmlNode.setNodeValue("//ns1:CurrencyTo",curIND)
xmlNode.setNodeValue("//ns1:RateDate",yesterdayDate)
xmlNode.setNodeValue("//ns1:Amount",amount)

def ModifiedXml =xmlNode.getXml()

//setting request xml with the modifies values and running the step 
testRunner.testCase.getTestStepByName("Conversion").setPropertyValue("Request",ModifiedXml)
testRunner.testCase.testSuite.project.testSuites["CurrencyConverterSuite"].testCases["GetConversionAmount"].testSteps["Conversion"].run(testRunner, context);

//Storing response of the service and saving it into Properties step
def res_Xml = context.expand('${Conversion#Response}')
def responseHolder = gutil.getXmlHolder(res_Xml)
responseHolder.namespaces['ns2'] = "http://tempuri.org/"
def res = responseHolder.getNodeValue("//ns2:GetConversionAmountResult")
testRunner.testCase.getTestStepByName("Properties").setPropertyValue("RateYDay",res)

// modifing xml for the next run with Today's date 
def ModXmlNode = gutil.getXmlHolder(reqXML)

ModXmlNode.namespaces['ns1']="http://tempuri.org/"
ModXmlNode.setNodeValue("//ns1:CurrencyFrom",curGBP)
ModXmlNode.setNodeValue("//ns1:CurrencyTo",curIND)
ModXmlNode.setNodeValue("//ns1:RateDate",todayDate)
ModXmlNode.setNodeValue("//ns1:Amount",amount)


def ModifiedXmlNew =ModXmlNode.getXml()

//setting request xml with the modifies values and running the step 
testRunner.testCase.getTestStepByName("Conversion").setPropertyValue("Request",ModifiedXmlNew)
testRunner.testCase.testSuite.project.testSuites["CurrencyConverterSuite"].testCases["GetConversionAmount"].testSteps["Conversion"].run(testRunner, context);

//Storing response of the service and saving it into Properties step
def TResXml = context.expand('${Conversion#Response}')

def resNewHolder = gutil.getXmlHolder(TResXml)
resNewHolder.namespaces['ns2'] = "http://tempuri.org/"
def resTDay = resNewHolder.getNodeValue("//ns2:GetConversionAmountResult")
testRunner.testCase.getTestStepByName("Properties").setPropertyValue("RateTDay",resTDay)
